@Composable
private fun ProductoCarritoItem(
    producto: ProductoCarrito,
    formatoMoneda: NumberFormat,
    onCantidadChanged: (Int) -> Unit,
    onEliminar: () -> Unit
) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surface.copy(alpha = 0.9f)
        )
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(12.dp),
            horizontalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            // Placeholder para imagen (color del producto)
            Box(
                modifier = Modifier
                    .size(80.dp)
                    .clip(RoundedCornerShape(8.dp))
                    .background(producto.color.color)
            )

            // Información del producto
            Column(
                modifier = Modifier.weight(1f),
                verticalArrangement = Arrangement.spacedBy(4.dp)
            ) {
                Text(
                    text = producto.nombre,
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold
                )

                if (producto.talla != null) {
                    Text(
                        text = "Talla: ${producto.talla.displayName}",
                        style = MaterialTheme.typography.bodySmall,
                        color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f)
                    )
                }

                Text(
                    text = "Color: ${producto.color.nombre}",
                    style = MaterialTheme.typography.bodySmall,
                    color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f)
                )

                Spacer(modifier = Modifier.height(4.dp))

                Row(
                    verticalAlignment = Alignment.CenterVertically,
                    horizontalArrangement = Arrangement.SpaceBetween,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    // Control de cantidad
                    Row(
                        verticalAlignment = Alignment.CenterVertically,
                        horizontalArrangement = Arrangement.spacedBy(8.dp)
                    ) {
                        IconButton(
                            onClick = { onCantidadChanged(producto.cantidad - 1) },
                            modifier = Modifier.size(32.dp)
                        ) {
                            Text(
                                text = "−",
                                style = MaterialTheme.typography.titleLarge
                            )
                        }

                        Text(
                            text = producto.cantidad.toString(),
                            style = MaterialTheme.typography.titleMedium,
                            modifier = Modifier.widthIn(min = 24.dp)
                        )

                        IconButton(
                            onClick = { onCantidadChanged(producto.cantidad + 1) },
                            modifier = Modifier.size(32.dp)
                        ) {
                            Icon(
                                imageVector = Icons.Default.Add,
                                contentDescription = "Aumentar cantidad",
                                modifier = Modifier.size(16.dp)
                            )
                        }
                    }

                    // Precio y botón eliminar
                    Column(
                        horizontalAlignment = Alignment.End
                    ) {
                        Text(
                            text = formatoMoneda.format(producto.subtotal()),
                            style = MaterialTheme.typography.titleMedium,
                            fontWeight = FontWeight.Bold,
                            color = MaterialTheme.colorScheme.primary
                        )
                        IconButton(
                            onClick = onEliminar,
                            modifier = Modifier.size(24.dp)
                        ) {
                            Icon(
                                imageVector = Icons.Default.Delete,
                                contentDescription = "Eliminar",
                                tint = MaterialTheme.colorScheme.error,
                                modifier = Modifier.size(20.dp)
                            )
                        }
                    }
                }
            }
        }
    }
}

package com.example.appajicolorgrupo4.ui.screens

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.automirrored.filled.ArrowBack
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import androidx.navigation.NavController
import com.example.appajicolorgrupo4.data.ProductoCarrito
import com.example.appajicolorgrupo4.ui.components.AppBackground
import com.example.appajicolorgrupo4.viewmodel.CarritoViewModel
import java.text.NumberFormat
import java.util.Locale

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun CheckoutScreenNew(
    navController: NavController,
    carritoViewModel: CarritoViewModel = viewModel()
) {
    val productos by carritoViewModel.productos.collectAsState()
    val subtotal = carritoViewModel.calcularSubtotal()
    val impuestos = carritoViewModel.calcularImpuestos()
    val costoEnvio = carritoViewModel.calcularCostoEnvio()
    val total = carritoViewModel.calcularTotal()
    val calificaEnvioGratis = carritoViewModel.calificaEnvioGratis()

    val formatoMoneda = remember {
        NumberFormat.getCurrencyInstance(Locale("es", "CL")).apply {
            maximumFractionDigits = 0
        }
    }

    var direccion by remember { mutableStateOf("") }
    var telefono by remember { mutableStateOf("") }
    var notasAdicionales by remember { mutableStateOf("") }

    AppBackground {
        Scaffold(
            topBar = {
                TopAppBar(
                    title = { Text("Confirmar Pedido") },
                    navigationIcon = {
                        IconButton(onClick = { navController.popBackStack() }) {
                            Icon(
                                imageVector = Icons.AutoMirrored.Filled.ArrowBack,
                                contentDescription = "Volver"
                            )
                        }
                    },
                    colors = TopAppBarDefaults.topAppBarColors(
                        containerColor = androidx.compose.ui.graphics.Color.Transparent
                    )
                )
            },
            containerColor = androidx.compose.ui.graphics.Color.Transparent
        ) { paddingValues ->
            LazyColumn(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(paddingValues)
                    .padding(16.dp),
                verticalArrangement = Arrangement.spacedBy(16.dp)
            ) {
                // Resumen de productos
                item {
                    Card(
                        modifier = Modifier.fillMaxWidth(),
                        colors = CardDefaults.cardColors(
                            containerColor = MaterialTheme.colorScheme.surface.copy(alpha = 0.9f)
                        )
                    ) {
                        Column(
                            modifier = Modifier.padding(16.dp),
                            verticalArrangement = Arrangement.spacedBy(12.dp)
                        ) {
                            Text(
                                text = "Resumen del Pedido",
                                style = MaterialTheme.typography.titleLarge,
                                fontWeight = FontWeight.Bold
                            )

                            HorizontalDivider()

                            productos.forEach { producto ->
                                ProductoResumenItem(producto, formatoMoneda)
                            }

                            HorizontalDivider()

                            // Desglose de costos
                            Column(
                                verticalArrangement = Arrangement.spacedBy(8.dp)
                            ) {
                                Row(
                                    modifier = Modifier.fillMaxWidth(),
                                    horizontalArrangement = Arrangement.SpaceBetween
                                ) {
                                    Text("Subtotal:")
                                    Text(formatoMoneda.format(subtotal))
                                }

                                Row(
                                    modifier = Modifier.fillMaxWidth(),
                                    horizontalArrangement = Arrangement.SpaceBetween
                                ) {
                                    Text("Impuestos (19% IVA):")
                                    Text(formatoMoneda.format(impuestos))
                                }

                                Row(
                                    modifier = Modifier.fillMaxWidth(),
                                    horizontalArrangement = Arrangement.SpaceBetween
                                ) {
                                    Text("Envío:")
                                    if (calificaEnvioGratis) {
                                        Text(
                                            text = "GRATIS",
                                            fontWeight = FontWeight.Bold,
                                            color = MaterialTheme.colorScheme.primary
                                        )
                                    } else {
                                        Text(formatoMoneda.format(costoEnvio))
                                    }
                                }

                                if (calificaEnvioGratis) {
                                    Text(
                                        text = "✓ Envío gratis por compra sobre ${formatoMoneda.format(20000)}",
                                        style = MaterialTheme.typography.bodySmall,
                                        color = MaterialTheme.colorScheme.primary
                                    )
                                }
                            }

                            HorizontalDivider()

                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween
                            ) {
                                Text(
                                    text = "Total a Pagar:",
                                    style = MaterialTheme.typography.titleLarge,
                                    fontWeight = FontWeight.Bold
                                )
                                Text(
                                    text = formatoMoneda.format(total),
                                    style = MaterialTheme.typography.titleLarge,
                                    fontWeight = FontWeight.Bold,
                                    color = MaterialTheme.colorScheme.primary
                                )
                            }
                        }
                    }
                }

                // Información de envío
                item {
                    Card(
                        modifier = Modifier.fillMaxWidth(),
                        colors = CardDefaults.cardColors(
                            containerColor = MaterialTheme.colorScheme.surface.copy(alpha = 0.9f)
                        )
                    ) {
                        Column(
                            modifier = Modifier.padding(16.dp),
                            verticalArrangement = Arrangement.spacedBy(12.dp)
                        ) {
                            Text(
                                text = "Información de Envío",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold
                            )

                            OutlinedTextField(
                                value = direccion,
                                onValueChange = { direccion = it },
                                label = { Text("Dirección de Envío *") },
                                modifier = Modifier.fillMaxWidth(),
                                minLines = 2,
                                placeholder = { Text("Calle, número, distrito, ciudad") }
                            )

                            OutlinedTextField(
                                value = telefono,
                                onValueChange = { telefono = it },
                                label = { Text("Teléfono de Contacto *") },
                                modifier = Modifier.fillMaxWidth(),
                                singleLine = true,
                                placeholder = { Text("999 999 999") }
                            )

                            OutlinedTextField(
                                value = notasAdicionales,
                                onValueChange = { notasAdicionales = it },
                                label = { Text("Notas Adicionales (Opcional)") },
                                modifier = Modifier.fillMaxWidth(),
                                minLines = 2,
                                placeholder = { Text("Referencias, indicaciones especiales, etc.") }
                            )
                        }
                    }
                }

                // Botón continuar
                item {
                    Column(verticalArrangement = Arrangement.spacedBy(12.dp)) {
                        Button(
                            onClick = { 
                                // Navegar a selección de método de pago
                                navController.navigate("payment_methods")
                            },
                            modifier = Modifier
                                .fillMaxWidth()
                                .height(56.dp),
                            enabled = direccion.isNotBlank() && telefono.isNotBlank()
                        ) {
                            Text(
                                text = "Seleccionar Método de Pago",
                                style = MaterialTheme.typography.titleMedium
                            )
                        }

                        if (direccion.isBlank() || telefono.isBlank()) {
                            Text(
                                text = "* Complete todos los campos obligatorios",
                                style = MaterialTheme.typography.bodySmall,
                                color = MaterialTheme.colorScheme.error,
                                modifier = Modifier.padding(horizontal = 16.dp)
                            )
                        }
                    }
                }

                item {
                    Spacer(modifier = Modifier.height(16.dp))
                }
            }
        }
    }
}

@Composable
private fun ProductoResumenItem(
    producto: ProductoCarrito,
    formatoMoneda: NumberFormat
) {
    Row(
        modifier = Modifier.fillMaxWidth(),
        horizontalArrangement = Arrangement.spacedBy(12.dp),
        verticalAlignment = Alignment.CenterVertically
    ) {
        // Color indicator
        Box(
            modifier = Modifier
                .size(40.dp)
                .clip(RoundedCornerShape(8.dp))
                .background(producto.color.color)
        )

        // Info
        Column(
            modifier = Modifier.weight(1f)
        ) {
            Text(
                text = producto.nombre,
                style = MaterialTheme.typography.bodyMedium,
                fontWeight = FontWeight.Medium
            )
            Text(
                text = buildString {
                    if (producto.talla != null) {
                        append("Talla ${producto.talla.displayName} • ")
                    }
                    append(producto.color.nombre)
                    append(" • Cant: ${producto.cantidad}")
                },
                style = MaterialTheme.typography.bodySmall,
                color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.6f)
            )
        }

        // Precio
        Text(
            text = formatoMoneda.format(producto.subtotal()),
            style = MaterialTheme.typography.bodyMedium,
            fontWeight = FontWeight.Bold
        )
    }
}

package com.example.appajicolorgrupo4.viewmodel

import android.app.Application
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.viewModelScope
import com.example.appajicolorgrupo4.data.local.database.AppDatabase
import com.example.appajicolorgrupo4.data.repository.UserRepository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch

data class LoginUiState(
    val correo: String = "",
    val clave: String = "",
    val correoError: String? = null,
    val claveError: String? = null,
    val isLoading: Boolean = false,
    val loginError: String? = null,
    val loginExitoso: Boolean = false
)

class LoginViewModel(application: Application) : AndroidViewModel(application) {

    // Repositorio
    private val repository: UserRepository

    init {
        val database = AppDatabase.getInstance(application)
        repository = UserRepository(database.userDao())
    }

    // Estado del login
    private val _estado = MutableStateFlow(LoginUiState())
    val estado: StateFlow<LoginUiState> = _estado

    // Actualizar correo
    fun actualizaCorreo(valor: String) {
        _estado.update {
            it.copy(
                correo = valor,
                correoError = null,
                loginError = null
            )
        }
    }

    // Actualizar clave
    fun actualizaClave(valor: String) {
        _estado.update {
            it.copy(
                clave = valor,
                claveError = null,
                loginError = null
            )
        }
    }

    // Validar campos
    private fun validarCampos(): Boolean {
        val estadoActual = _estado.value
        var valido = true
        var correoError: String? = null
        var claveError: String? = null

        if (estadoActual.correo.isBlank()) {
            correoError = "El correo es obligatorio"
            valido = false
        } else if (!estadoActual.correo.contains("@")) {
            correoError = "Correo inválido"
            valido = false
        }

        if (estadoActual.clave.isBlank()) {
            claveError = "La contraseña es obligatoria"
            valido = false
        }

        _estado.update {
            it.copy(
                correoError = correoError,
                claveError = claveError
            )
        }

        return valido
    }

    // Iniciar sesión
    fun iniciarSesion(onSuccess: () -> Unit) {
        if (!validarCampos()) {
            return
        }

        val estadoActual = _estado.value
        _estado.update { it.copy(isLoading = true, loginError = null) }

        viewModelScope.launch {
            val resultado = repository.login(
                correo = estadoActual.correo,
                clave = estadoActual.clave
            )

            resultado.onSuccess { user ->
                _estado.update {
                    it.copy(
                        isLoading = false,
                        loginExitoso = true,
                        loginError = null
                    )
                }
                onSuccess()
            }.onFailure { error ->
                _estado.update {
                    it.copy(
                        isLoading = false,
                        loginExitoso = false,
                        loginError = error.message ?: "Error al iniciar sesión"
                    )
                }
            }
        }
    }

    // Limpiar errores
    fun limpiarErrores() {
        _estado.update {
            it.copy(
                correoError = null,
                claveError = null,
                loginError = null
            )
        }
    }
}

package com.example.apppolera_ecommerce_grupo4.ui.screens

import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.input.PasswordVisualTransformation
import androidx.compose.ui.unit.dp
import androidx.navigation.NavController
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.apppolera_ecommerce_grupo4.viewmodel.UsuarioViewModel

@Composable
fun RegistroScreen(
    navController: NavController,

) {
    val estado by viewModel.estado.collectAsState()

    Column(
        modifier = Modifier
            .fillMaxSize()
            .padding(16.dp),
        verticalArrangement = Arrangement.spacedBy(12.dp)
    ) {
        // Campo Nombre
        OutlinedTextField(
            value = estado.nombre,
            onValueChange = viewModel::actualizaNombre,
            label = { Text("Nombre") },
            modifier = Modifier.fillMaxWidth(),
            isError = estado.errores.nombre != null,
            supportingText = { estado.errores.nombre?.let { Text(it, color = Color.Red) } }
        )

        // Campo Correo
        OutlinedTextField(
            value = estado.correo,
            onValueChange = viewModel::actualizaCorreo,
            label = { Text("Correo electrónico") },
            modifier = Modifier.fillMaxWidth(),
            isError = estado.errores.correo != null,
            supportingText = { estado.errores.correo?.let { Text(it, color = Color.Red) } }
        )

        // Campo Clave
        OutlinedTextField(
            value = estado.clave,
            onValueChange = viewModel::actualizaClave,
            label = { Text("Clave") },
            modifier = Modifier.fillMaxWidth(),
            visualTransformation = PasswordVisualTransformation(),
            isError = estado.errores.clave != null,
            supportingText = { estado.errores.clave?.let { Text(it, color = Color.Red) } }
        )

        // Campo Dirección
        OutlinedTextField(
            value = estado.direccion,
            onValueChange = viewModel::actualizaDireccion,
            label = { Text("Dirección") },
            modifier = Modifier.fillMaxWidth(),
            isError = estado.errores.direccion != null,
            supportingText = { estado.errores.direccion?.let { Text(it, color = Color.Red) } }
        )

        // Checkbox términos
        Row(verticalAlignment = androidx.compose.ui.Alignment.CenterVertically) {
            Checkbox(
                checked = estado.aceptaTerminos,
                onCheckedChange = viewModel::actualizaAceptaTerminos
            )
            Text("Acepto los términos y condiciones")
        }
        if (estado.errores.aceptaTerminos != null) {
            Text(estado.errores.aceptaTerminos!!, color = Color.Red)
        }

        // Botón registrar
        Button(
            onClick = {
                if (viewModel.validarFormulario()) {
                    navController.navigate(Routes.RESUMEN)
                }
            },
            modifier = Modifier.fillMaxWidth()
        ) {
            Text("Registrarse")
        }
    }
}

